import { motion } from 'framer-motion';
import React from 'react';
import './MarketStatus.css';

const MarketStatus = ({ stocks, marketStats }) => {
  const getMarketStatus = () => {
    const now = new Date();
    const istTime = new Date(now.toLocaleString("en-US", {timeZone: "Asia/Kolkata"}));
    const hour = istTime.getHours();
    const day = istTime.getDay(); // 0 = Sunday, 6 = Saturday

    // Market hours: Monday to Friday, 9:00 AM to 3:30 PM IST
    if (day === 0 || day === 6) {
      return { status: 'closed', message: 'Market is closed (Weekend)', color: 'danger' };
    }

    if (hour < 9) {
      return { status: 'pre-market', message: 'Pre-market session', color: 'warning' };
    } else if (hour >= 9 && hour < 15) {
      return { status: 'open', message: 'Market is open', color: 'success' };
    } else if (hour >= 15 && hour < 16) {
      return { status: 'closing', message: 'Market closing soon', color: 'warning' };
    } else {
      return { status: 'closed', message: 'Market is closed', color: 'danger' };
    }
  };

  const getTopGainers = () => {
    return stocks
      .filter(stock => stock.isPositive)
      .sort((a, b) => b.changePercent - a.changePercent)
      .slice(0, 5);
  };

  const getTopLosers = () => {
    return stocks
      .filter(stock => !stock.isPositive)
      .sort((a, b) => a.changePercent - b.changePercent)
      .slice(0, 5);
  };

  const getMostActive = () => {
    return stocks
      .sort((a, b) => Math.abs(b.change) - Math.abs(a.change))
      .slice(0, 5);
  };

  const marketStatus = getMarketStatus();
  const topGainers = getTopGainers();
  const topLosers = getTopLosers();
  const mostActive = getMostActive();

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(price);
  };

  const formatChange = (change) => {
    return new Intl.NumberFormat('en-IN', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(Math.abs(change));
  };

  return (
    <div className="market-status">
      {/* Market Status Overview */}
      <motion.div 
        className="status-overview"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
      >
        <div className={`status-card ${marketStatus.color}`}>
          <div className="status-icon">
            <i className={`fas fa-${marketStatus.status === 'open' ? 'play' : marketStatus.status === 'closed' ? 'stop' : 'pause'}`}></i>
          </div>
          <div className="status-content">
            <h3>Market Status</h3>
            <p className="status-message">{marketStatus.message}</p>
            <div className="status-time">
              <i className="fas fa-clock"></i>
              <span>{new Date().toLocaleTimeString('en-IN', { timeZone: 'Asia/Kolkata' })} IST</span>
            </div>
          </div>
        </div>

        <div className="quick-stats">
          <div className="stat-item">
            <span className="stat-label">Total Indexes</span>
            <span className="stat-value">{marketStats.total}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Gainers</span>
            <span className="stat-value text-success">{marketStats.gainers}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Losers</span>
            <span className="stat-value text-danger">{marketStats.losers}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Avg Change</span>
            <span className={`stat-value ${parseFloat(marketStats.avgChange) >= 0 ? 'text-success' : 'text-danger'}`}>
              {parseFloat(marketStats.avgChange) >= 0 ? '+' : ''}{marketStats.avgChange}%
            </span>
          </div>
        </div>
      </motion.div>

      {/* Market Leaders */}
      <div className="market-leaders">
        <div className="leaders-grid">
          {/* Top Gainers */}
          <motion.div 
            className="leader-section gainers"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            <div className="section-header">
              <h3>
                <i className="fas fa-arrow-up text-success"></i>
                Top Gainers
              </h3>
            </div>
            <div className="leader-list">
              {topGainers.map((stock, index) => (
                <div key={stock.symbol} className="leader-item">
                  <div className="rank">{index + 1}</div>
                  <div className="stock-info">
                    <div className="stock-name">{stock.name}</div>
                    <div className="stock-symbol">{stock.symbol}</div>
                  </div>
                  <div className="stock-change text-success">
                    <div className="change-amount">+₹{formatChange(stock.change)}</div>
                    <div className="change-percent">+{formatChange(stock.changePercent)}%</div>
                  </div>
                </div>
              ))}
            </div>
          </motion.div>

          {/* Top Losers */}
          <motion.div 
            className="leader-section losers"
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            <div className="section-header">
              <h3>
                <i className="fas fa-arrow-down text-danger"></i>
                Top Losers
              </h3>
            </div>
            <div className="leader-list">
              {topLosers.map((stock, index) => (
                <div key={stock.symbol} className="leader-item">
                  <div className="rank">{index + 1}</div>
                  <div className="stock-info">
                    <div className="stock-name">{stock.name}</div>
                    <div className="stock-symbol">{stock.symbol}</div>
                  </div>
                  <div className="stock-change text-danger">
                    <div className="change-amount">-₹{formatChange(stock.change)}</div>
                    <div className="change-percent">-{formatChange(stock.changePercent)}%</div>
                  </div>
                </div>
              ))}
            </div>
          </motion.div>

          {/* Most Active */}
          <motion.div 
            className="leader-section active"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.4 }}
          >
            <div className="section-header">
              <h3>
                <i className="fas fa-fire text-warning"></i>
                Most Active
              </h3>
            </div>
            <div className="leader-list">
              {mostActive.map((stock, index) => (
                <div key={stock.symbol} className="leader-item">
                  <div className="rank">{index + 1}</div>
                  <div className="stock-info">
                    <div className="stock-name">{stock.name}</div>
                    <div className="stock-symbol">{stock.symbol}</div>
                  </div>
                  <div className={`stock-change ${stock.isPositive ? 'text-success' : 'text-danger'}`}>
                    <div className="change-amount">
                      {stock.isPositive ? '+' : '-'}₹{formatChange(stock.change)}
                    </div>
                    <div className="change-percent">
                      {stock.isPositive ? '+' : '-'}{formatChange(stock.changePercent)}%
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </motion.div>
        </div>
      </div>
    </div>
  );
};

export default MarketStatus;
